package adventofcode;

/**
 * Why Did you create this class? what does it do?
 */
public class Captcha_1 {
    public static void main(String[] args) {
        System.out.println(
                solve(""));
        System.out.println(solve("1122"));
        System.out.println(solve("1111"));
        System.out.println(solve("1234"));
        System.out.println(solve("91212129"));
        System.out.println("---------------");
        System.out.println(
                solve2(""));
        System.out.println(solve2("1212"));
        System.out.println(solve2("1221"));
        System.out.println(solve2("123425"));
        System.out.println(solve2("123123"));
        System.out.println(solve2("12131415"));
    }

    private static int solve(String s) {
        if (s == null || s.length() == 0)
            return 0;
        if (s.length() == 1)
            return Integer.parseInt(s);
        int sum = 0;
        for (int i = 1; i < s.length(); i++) {
            if (s.charAt(i - 1) == s.charAt(i))
                sum += (s.charAt(i) - '0');
        }
        if (s.charAt(s.length() - 1) == s.charAt(0))
            sum += (s.charAt(0) - '0');

        return sum;
    }

    private static int solve2(String s) {
        if (s == null || s.length() == 0)
            return 0;
        if (s.length() == 1)
            return Integer.parseInt(s);
        int sum = 0;
        int n = s.length();
        for (int i = 0; i < s.length(); i++) {
            char current = s.charAt(i);
            char next = s.charAt((i + n / 2) % n);
            if (next == current)
                sum += (current - '0');
        }
        return sum;
    }

}
